import React, { useState,useEffect,useContext } from "react";
import axios from 'axios'
import NavBar from './NavBar';
import { useHistory } from "react-router";
import NavLogin from "./NavLogin";
import {UserContext} from '../context/UserContext'

function Post() {
  var [posts, setPosts] = useState([]);
  var [user,setUser] = useState({})
  var [like,setLike] = useState([])

  let history = useHistory()
  const JWTtoken = localStorage.getItem('JWTtoken')

    // var [posts, setPosts] = useState([ { username: "Pranshu_Singh", image: `${image}`, caption: "Hello" },
    // { username: "Pravin_Singh", image: `${image}`, caption: "Ciao" },]);
useEffect(()=>{

  axios.get("http://localhost:3001/posts",{
    headers:{
      'auth-token':JWTtoken
    }
  })
  .then(res=>{
  if(res.status===200){
    console.log(res)
    setPosts(res.data)
  }
  })
  .catch(err=>{
    console.log(err)
    history.push('/unauthorized')
  })

  axios.get("http://localhost:3001/user",{
          headers:{
              'auth-token':JWTtoken
            }
          })
        .then(res=>{
          console.log(res)
          setUser(res.data)
        })
},[])

const likeCounter = (id) => {
    axios.put("http://localhost:3001/posts/like",{
      headers:{
          'auth-token':JWTtoken
        },
      body:JSON.stringify({
        postId:id
      })
        })
        .then(result=>{
          console.log(result)
      })
    setLike(user.name)
}


var Button
if(JWTtoken)
  Button = <NavLogin></NavLogin>
else
  Button = <NavBar></NavBar>

  return (
    <div>
      {Button}
        
    
        {posts.map((post) => (
          <>
            <main style={{border:"2px solid black"}}>
            <div className="head">
              <div className="image">
                <i className="fas fa-user-circle"></i>
              </div>
              <div className="username">{post.postedBy.name}</div>
            </div>
            <div className="body">
              <img style={{maxHeight:"100%",maxWidth:"100%",objectFit:"cover"}} src={post.image}></img>
            </div>
            <div className="foot">
              <div className="like">
                <button style={{border:"none",background:"white"}} onClick={likeCounter(post._id)}><i className="far fa-heart fa-lg"></i></button>{like}
              </div>
              <div className="comment">
                <i className="far fa-comment fa-lg"></i>
              </div>
            </div>
            <div className="caption">
              {post.username}&nbsp;
              {post.caption}
            </div>
            </main>
          </>
        

        ))}

         
    </div>
  );
}

export default Post;
