import './App.css';
import {useState,useEffect} from 'react'
import NavBar from './components/NavBar';
import Post from './components/Post'
import 'bootstrap/dist/css/bootstrap.min.css';
import image from "../src/assets/images/image.jfif";
import CreatePost from './components/CreatePost';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


function App() {
  var [posts, setPosts] = useState([ { username: "Pranshu_Singh", image: `${image}`, caption: "Hello" },
    { username: "Pravin_Singh", image: `${image}`, caption: "Ciao" },]);


    // useEffect(()=>{
    //   let data = localStorage.getItem('post-list')
    //   if(data){
    //     setPosts(JSON.parse(data))
    //   }
    // },[])

    // useEffect(()=>{
    //   localStorage.setItem('post-list',JSON.stringify(posts))
    // })

  return (
    <Router>
    <div className="App">
      <Switch>
      <Route path='/' exact ><NavBar></NavBar></Route>
      <Route path='/create-post' exact ><NavBar></NavBar><CreatePost posts={posts} setPosts={setPosts} /></Route>
      <Route path='/post' exact ><NavBar></NavBar><Post posts={posts} setPosts={setPosts}></Post></Route>
      </Switch>
    </div>
    </Router>
  );
}

export default App;
