import axios from 'axios'
import React,{useEffect,useState,useContext} from 'react'
import NavBar from './NavBar';
import { useHistory } from 'react-router';
import NavLogin from './NavLogin';
import 'bootstrap/dist/css/bootstrap.css'
import {UserContext} from '../context/UserContext'

function Profile() {
  var [userposts,setUserposts] = useState([])
  var [user,setUser] = useState([])





  let history = useHistory()
  const JWTtoken = localStorage.getItem("JWTtoken")

    useEffect(() => {

       const userid = localStorage.getItem("userId")
       
        axios.get("http://localhost:3001/posts/myposts",{
            headers:{
                'auth-token':JWTtoken
            }
        })
        .then(res=>{
          if(res.status===200){
            console.log(res)
            localStorage.setItem("userId",res.data[0].postedBy._id)
            setUserposts(res.data)
          }
          if(res.status===400){
            localStorage.removeItem("JWTtoken")
          }
        })
        .catch(err=>{
          console.log(err)
          history.push('/unauthorized')
        
        })

        axios.get(`http://localhost:3001/user/${userid}`)
        .then(res=>{
          console.log(res.data)
          setUser(res)
        })


        
    }, [])

    var Button
    if(JWTtoken)
      Button = <NavLogin></NavLogin>
    else
      Button = <NavBar></NavBar>

    return (
        <div>
          {Button}
          <div className="container" style={{height:"200px"}}>
            <div className="row">
              <div className="col-6">
                  <div className="profile-img">
                      image
                  </div>
              </div>
              <div className="col-6">
                {user.name}
              </div>

            </div>
          </div>

            {userposts.map(post=>(
                <>
                <main>
                <div className="head">
                  <div className="image">
                    <i className="fas fa-user-circle"></i>
                  </div>
                  <div className="username">{post.postedBy.name}</div>
                </div>
                <div className="body">
                  <img src={post.image}></img>
                </div>
                <div className="foot">
                  <div className="like">
                    <i className="far fa-heart fa-lg"></i>
                  </div>
                  <div className="comment">
                    <i className="far fa-comment fa-lg"></i>
                  </div>
                </div>
                <div className="caption">
                  {post.username}&nbsp;
                  {post.caption}
                </div>
                </main>
              </>
            ))}
        </div>
    )
}

export default Profile
