import React,{useState} from "react";
import image from '../assets/images/image.jfif'

function Post() {

    var [posts,setPosts] = useState([])

    posts = [{
        username:"Pranshu_Singh",
        image:`${image}`,
        caption:"Hello"
    }]

  return (
    <div>
      <main>
          {posts.map(post=>(
              <>
        <div className="head">
        <div className="image"><i class="fas fa-user-circle"></i></div>
        <div className="username">{post.username}</div>
</div>
<div className="body">
    <img src={post.image}></img>
</div>
<div className="foot">
    <div className="like"><i class="far fa-heart fa-lg"></i></div>
    <div className="comment"><i class="far fa-comment fa-lg"></i></div>
</div>
    <div className="caption">{post.username}{post.caption}</div>
    </>
          ))}

      </main>
    </div>
  );
}

export default Post;
