import React, { useState } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";
import axios from 'axios'

function CreatePost({posts,setPosts}) {
    var [username,setUsername] = useState("")
    var [caption,setCaption] = useState("")
    var [image,setImage] = useState()

    const submitHandler = (e) => {
        e.preventDefault()
        setPosts([...posts,{username:username,image:image,caption:caption}])
        setUsername("")
        setCaption("")
        const data ={
          username: username,
          caption:caption ,
        };
        axios.post("http://localhost/3001/posts",data)
    }
  return (
    <div>
        <form onSubmit={submitHandler}>
            <input value={username} onChange={e=>{setUsername(e.target.value)}} type="text"></input>
            <input value={caption} onChange={e=>{setCaption(e.target.value)}} type="text"></input>
            <input value={image} onChange={e=>{setImage(e.target.value)}} type="file"></input>
            <input type="submit"></input>
        </form>
    </div>
  );
}

export default CreatePost;
