import axios from 'axios'
import React,{useEffect,useState,useContext} from 'react'
import NavBar from './NavBar';
import { useHistory } from 'react-router';
import NavLogin from './NavLogin';
import 'bootstrap/dist/css/bootstrap.css'
import {UserContext} from '../context/UserContext'
import profilepic from '../assets/images/image.jfif'

function Profile() {
  var [userposts,setUserposts] = useState([])
  var [user,setUser] = useState({})



  let history = useHistory()
  const JWTtoken = localStorage.getItem("JWTtoken")


    useEffect(() => {

      
        axios.get("http://localhost:3001/posts/myposts",{
            headers:{
                'auth-token':JWTtoken
            }
        })
        .then(res=>{
          if(res.status===200){
            console.log(res)
            setUserposts(res.data)
          }
        })
        .catch(err=>{
          console.log(err)
          history.push('/unauthorized')
        
        })

        axios.get("http://localhost:3001/user",{
          headers:{
              'auth-token':JWTtoken
            }
          })
        .then(res=>{
          console.log(res)
          setUser(res.data)
        })
        
    }, [])

  



    var Button
    if(JWTtoken)
      Button = <NavLogin></NavLogin>
    else
      Button = <NavBar></NavBar>

    return (
        <div>
          {Button}
          <div id="profile-cont" className="container">
           <div className="row" >
              <div className="col-4" style={{display:"flex",justifyContent:"center",alignItems:"center"}}>
                <img class="profile-pic" src={profilepic}></img>
              </div>
              <div className="col-8">
                  <div className="row">
                    {user.name}
                  </div>
              </div>
           </div>
          </div>

            {userposts.map(post=>(
                <>
                <main>
                <div className="head">
                  <div className="image">
                    <i className="fas fa-user-circle"></i>
                  </div>
                  <div className="username">{post.postedBy.name}</div>
                </div>
                <div className="body">
                  <img src={post.image}></img>
                </div>
                <div className="foot">
                  <div className="like">
                    <i className="far fa-heart fa-lg"></i>
                  </div>
                  <div className="comment">
                    <i className="far fa-comment fa-lg"></i>
                  </div>
                </div>
                <div className="caption">
                  {post.username}&nbsp;
                  {post.caption}
                </div>
                </main>
              </>
            ))}
        </div>
    )
}

export default Profile
