import React from "react";
import { Form, Button, Row, Col } from "react-bootstrap";

function CreatePost({posts}) {

    const submitHandler = (e) => {
        e.preventDefault()
        var uname = document.getElementById("username").value;
        var cap = document.getElementById("caption").value;
        const postobj = {username:uname,caption:cap}
        posts.unshift(postobj)
    }

  return (
    <div>
     <form onSubmit={submitHandler}>
         <input type="text" id="username"></input><br></br>
         <input type="text" id="caption"></input><br></br>
         <input type="submit"></input>
     </form>
         
     {posts.map((post) => (
          <>
            <main key={post.caption}>
            <div key={post.caption} className="head">
              <div key={post.caption} className="image">
                <i key={post.caption} className="fas fa-user-circle"></i>
              </div>
              <div key={post.caption} className="username">{post.username}</div>
            </div>
            <div key={post.caption} className="body">
              <img key={post.caption} src={post.image}></img>
            </div>
            <div key={post.caption} className="foot">
              <div key={post.caption} className="like">
                <i key={post.caption} className="far fa-heart fa-lg"></i>
              </div>
              <div key={post.caption} className="comment">
                <i key={post.caption} className="far fa-comment fa-lg"></i>
              </div>
            </div>
            <div key={post.caption} className="caption">
              {post.username}&nbsp;
              {post.caption}
            </div>
            </main>
          </>
        ))}
    </div>
  );
}

export default CreatePost;
