import React, { useState } from "react";
import CreatePost from './CreatePost';
import image from "../assets/images/image.jfif";

function Post() {


    var [posts, setPosts] = useState([ { username: "Pranshu_Singh", image: `${image}`, caption: "Hello" },
    { username: "Pravin_Singh", image: `${image}`, caption: "Ciao" },]);


    const addPost = (username,image,caption) => {
        setPosts([...posts,{username,image:url("http://127.0.0.1:8887/1.jpg"),caption}])
        
    }

  return (
    <div>
    
        {posts.map((post) => (
          <>
            <main>
            <div className="head">
              <div className="image">
                <i className="fas fa-user-circle"></i>
              </div>
              <div className="username">{post.username}</div>
            </div>
            <div className="body">
              <img src={post.image}></img>
            </div>
            <div className="foot">
              <div className="like">
                <i className="far fa-heart fa-lg"></i>
              </div>
              <div className="comment">
                <i className="far fa-comment fa-lg"></i>
              </div>
            </div>
            <div className="caption">
              {post.username}&nbsp;
              {post.caption}
            </div>
            </main>
          </>
        ))}
          <CreatePost addPost={addPost}/>
    </div>
  );
}

export default Post;
