function requireAuth(nextState, replace, next) {
    var login = localStorage.getItem('JWTtoken')
  if (!login) {
    replace({
      pathname: "/unknown",
      state: {nextPathname: nextState.location.pathname}
    });
  }
  next();
}

export default requireAuth
