import React, { useState } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";

function CreatePost({addPost}) {
    var [username,setUsername] = useState("")
    var [caption,setCaption] = useState("")
    var [image,setImage] = useState()

    const submitHandler = (e) => {
        e.preventDefault()
        addPost(username,image,caption)
        setUsername("")
        setCaption("")
        setImage()
    }
  return (
    <div>
        <form onSubmit={submitHandler}>
            <input onChange={e=>{setUsername(e.target.value)}} type="text"></input>
            <input onChange={e=>{setCaption(e.target.value)}} type="text"></input>
            <input onChange={e=>{setImage(e.target.value)}} type="file"></input>
            <input type="submit"></input>
        </form>
    </div>
  );
}

export default CreatePost;
